import React,{ useState } from "react";
import { AddHeader, AddForm ,Form} from "./style";
import { db, storage } from "../../firebase/firebase";
import { collection, addDoc } from "firebase/firestore";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage"; // Storage functions

export default function AddProduct() {

     const [brand,setBrand] = useState<string>('')
     const [name,setName] = useState<string>('')
     const [price, setPrice] = useState<string>("");
     const [image,setImage] = useState<File | null>(null)

    // Handle form submission 

    const handleSubmit = async (e:React.FormEvent)=> {
      e.preventDefault()

      if(!brand || !name || !price ||!image){
        alert("Please fill out all fields!");
        return;
      }

      const storageRef = ref(storage, `products/${image.name}`);
      const uploadTask = uploadBytesResumable(storageRef, image);

      uploadTask.on(
        "state_changed",
        () => {}, 
        (error) => {
          console.error("Error uploading image:", error);
          alert("Error uploading image");
        },
        async () => {
          // Handle successful upload and get the download URL
          const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);

          // Add the new product to Firestore with the image URL
          try {
            const docRef = await addDoc(collection(db, "products"), {
              brand,
              name,
              price,
              imageUrl: downloadURL, // Save the image URL in Firestore
            });
            console.log("Document written with ID: ", docRef.id);
            alert("Product added successfully!");
          } catch (e) {
            console.error("Error adding document: ", e);
            alert("Error adding product");
          }
        }
      );

    }


    return (
     <>
       <AddHeader/>
       <AddForm>
         <h1 id="heading">Post your Ad</h1>
         <Form>
             <form onSubmit={ handleSubmit}>
               <label >Brand</label>
               <input
                value={brand}
                type="text"
                onChange={(e)=> setBrand(e.target.value)}
                 />
               <label htmlFor=""> Name</label>
               <input
                value={name}
                type="text"
                onChange={(e)=> setName(e.target.value)}
                 />
               <label htmlFor="">Price</label>
               <input
                value={price}
                type="text"
                onChange={(e)=> setPrice(e.target.value)}
                 />
               <label htmlFor="">Image</label>
               <input
                type="file"
                onChange={(e)=> setImage(e.target.files ? e.target.files[0] : null)}
              />

               <label htmlFor="">Submit Form</label>
               <button type="submit">Submit</button>

             </form>
         </Form>
       </AddForm>
     </>
    )

}
