import { useNavigate } from 'react-router-dom'
import { useEffect, useState, useReducer } from 'react'
import { fetchData } from './fetch'
import { productContext } from '../../contexts/products'

import NavList from '../../componants/NavList/NavList'
import Mhead from '../../componants/Main/Mhead'
import MainContiner from '../../componants/Main/Main'
import { Header, Logo, DropDown, Search, Language, AddLogin, Nav, Main } from './styles'

import search from '../../images/search.png'
import CustomButton from '../../images/CustomButton.png'


export interface productProps {
   category: string;
   brand: string;
   name: string;
   price: string;
   imageURL: string;
}

enum ActionTypes {
   ALL = "ALL",
   CARS = "CARS",
   BIKES = 'BIKES'
}

interface types {
   type : 'all' | 'cars' | 'bikes'
}

const reducer = (state:productProps[],action:{type:ActionTypes})=> {

    switch(action.type) {
         case  ActionTypes.ALL:
         return state;

         case  ActionTypes.CARS:
         return state.filter((x)=> x.category ==='car')

         case ActionTypes.BIKES:
         return state.filter((x)=> x.category ==='bike')
    }

}

export default function Home() {

   const navigate = useNavigate()
   // const [products, setProducts] = useState<productProps[]>([])
   let [products,dispatch] = useReducer(reducer,[])


   useEffect(() => {
      const fetProdctsData = async () => {
         const products: productProps[] = await fetchData()

         if (products.length > 0) {
            // setProducts(products)

         }
      }

      fetProdctsData()

   }, [])

   return (
      <>
         <productContext.Provider value={{ products}}>
            <Header>
               <Logo />
               <DropDown />
               <Search>
                  <input type="text" placeholder='Find Cars, Mobile Phones and more...' />
                  <button > <span> <img src={search} /></span> </button>

               </Search>
               <Language />
               <AddLogin >
                  <div id='Login'>
                     <p >Login</p>
                  </div>
                  <div onClick={() => navigate('/add')} id='custom-button'>
                     <img src={CustomButton} alt="" />
                  </div>
               </AddLogin>
            </Header>

            <Nav>
               <NavList />
            </Nav>

            <Main>
               <Mhead />
               <MainContiner />
            </Main>
         </productContext.Provider>
      </>
   )
}
