      if(action==='all') {
        console.log('al')
        setDatas([...products])
      }
      else if(action ==='cars'){
        const cars = products.filter(product => product.category ==='car')
        setDatas(cars)

      }
      else if(action === 'bikes') {
        const bikes = products.filter(product => product.category === 'bike')
        setDatas(bikes)

      }

    }

    return (
        <>
         <cardContext.Provider value={{productss : datas, setfetchedData : setDatas}} >
         <List>
            <li onClick={()=> handleFilter('all')}>All</li>
            <li onClick={()=> handleFilter('cars')}>Car</li>
            <li onClick={()=> handleFilter('bikes')}>Bike</li>
         </List>
         </cardContext.Provider>
        </>
    )

}
