import {productContext} from '../../contexts/products'
import {List} from './style'
import { productProps } from '../../pages/Home/Home'
import { useContext, useState,useEffect } from 'react'
import { cardContext } from '../../contexts/card'


export default function Nav() {

    const {products} = useContext(productContext)
    const [datas,setDatas] = useState<productProps[]>([])

    useEffect(()=> {
      if(products.length>0) {
        setDatas([...products])
      }
    },[products])

    const handleFilter = (action:string)=> {

      if(action==='all') {
         setDatas(datas)
      }
      else if(action =='cars'){
        setDatas(datas.filter(product => product.category ==='car'))
      }
      else if(action == 'bikes') {
        setDatas(datas.filter(product => product.category == 'bike')  )
      }

    }


    return (
        <>
         <cardContext.Provider value={{productss : datas, setfetchedData : setDatas}} >
         <List>
            <li onClick={()=> handleFilter('all')}>All</li>
            <li onClick={()=> handleFilter('cars')}>Car</li>
            <li onClick={()=> handleFilter('bikes')}>Bike</li>
         </List>
         </cardContext.Provider>
        </>
    )

}
