import { cardContext } from "../../contexts/card"

export default function Card () {

    const {products,setProductsFetched} = useContext(productContext)
    const {productss,se} = useContext(cardContext)

    const [datas,setfetchedData] = useState(products)

    useEffect(()=> {

      if(products.length> 0){
        setfetchedData(products)
